use educayso_facae;
create view asignaturadeldocente1 as select asdo.idasignaturadeldocente ,asdo.iddocente,pers.idpersona, concat(COALESCE(pers.apellidos,''),"  ",COALESCE(pers.nombres,'')) as eldocente, asig.idasignatura,concat(COALESCE(arco.nombre,'')," - ",COALESCE(asig.nombre,'')) as laasignatura, niac.nombre as nivel,(select docu.archivopdf from documento docu where docu.iddocumento=asdo.iddocumento) as archivopdf  from asignaturadeldocente asdo, docente doce, persona pers,asignatura asig,nivelacademico niac,areaconocimiento arco where  asdo.iddocente=doce.iddocente and doce.idpersona=pers.idpersona and asdo.idasignatura=asig.idasignatura and  asig.idnivelacademico=niac.idnivelacademico and asig.idareaconocimiento=arco.idareaconocimiento;
