
use educayso_facae;
drop view calidadcarrera1;
create view calidadcarrera1 as select caca.idcalidadcarrera, caca.nombre,  caca.detalle, caca.iddepartamento,depa.nombre as eldepartamento,caca.archivo,caca.idcriteriocalidad,crca.nombre as elcriteriocalidad,caca.idsubcriteriocalidad,scca.nombre as elsubcriteriocalidad,caca.idindicadorcalidad,inca.nombre as elindicadorcalidad,caca.codigo, crca.inicial as inicialcriterio,scca.inicial as inicialsubcriterio,inca.inicial as inicialindicadorcalidad,tica.idtipocalidad,tica.nombre as eltipocalidad,tica.color  from calidadcarrera caca,departamento depa,criteriocalidad crca,subcriteriocalidad scca, indicadorcalidad inca, tipocalidad tica where caca.iddepartamento=depa.iddepartamento and caca.idcriteriocalidad=crca.idcriteriocalidad and caca.idsubcriteriocalidad=scca.idsubcriteriocalidad and caca.idindicadorcalidad = inca.idindicadorcalidad and caca.idtipocalidad=tica.idtipocalidad; 
