
use educayso_facae;
drop view distributivodocente2;
create view distributivodocente2 as select peac.nombrecorto as elperiodo,niac.nombre as nivel, para.nombre as paralelo,  concat(COALESCE(pers.apellidos,''),"  ",COALESCE(pers.nombres,'')) as eldocente, asig.nombre as laasignatura, (select dise.nombre from diasemana dise where dise.iddiasemana=jodo.iddiasemana) as dia,jodo.horainicio,jodo.duracionminutos,  dido.iddistributivodocente, dido.iddocente from distributivodocente dido, periodoacademico peac, asignaturadocente asdo,docente doce, persona pers,asignatura asig,nivelacademico niac, paralelo para,jornadadocente jodo where dido.idperiodoacademico=peac.idperiodoacademico and  dido.iddocente=doce.iddocente and doce.idpersona=pers.idpersona and dido.iddistributivodocente=asdo.iddistributivodocente and asdo.idparalelo=para.idparalelo and asdo.idasignatura=asig.idasignatura and asig.idnivelacademico=niac.idnivelacademico and asdo.idasignaturadocente =jodo.idasignaturadocente;
