use educayso_facae; 
create view examencomplexivo3 as select ticu.idexamencomplexivo, dtic.iddocumentoexamencomplexivo,docu.iddocumento,docu.asunto,docu.fechaelaboracion,docu.archivopdf,tido.descripcion as eltipodocumento, dire.ruta,orde.nombre as elordenador from examencomplexivo ticu,  documento docu, documentoexamencomplexivo dtic, tipodocu tido, directorio dire,ordenador orde where ticu.idexamencomplexivo=dtic.idexamencomplexivo and dtic.iddocumento=docu.iddocumento and docu.idtipodocu=tido.idtipodocu and docu.iddirectorio=dire.iddirectorio and docu.idordenador=orde.idordenador; 
