
use educayso_facae;
create view informacioncarrera1 as select caca.idinformacioncarrera, caca.nombre,  caca.detalle, caca.iddepartamento,depa.nombre as eldepartamento,caca.archivo,caca.idcriteriocalidad,crca.nombre as elcriteriocalidad,caca.idsubcriteriocalidad,scca.nombre as elsubcriteriocalidad,caca.idindicadorcalidad,inca.nombre as elindicadorcalidad,caca.solicitante, crca.inicial as inicialcriterio,scca.inicial as inicialsubcriterio,inca.inicial as inicialindicadorcalidad  from informacioncarrera caca,departamento depa,criteriocalidad crca,subcriteriocalidad scca, indicadorcalidad inca where caca.iddepartamento=depa.iddepartamento and caca.idcriteriocalidad=crca.idcriteriocalidad and caca.idsubcriteriocalidad=scca.idsubcriteriocalidad and caca.idindicadorcalidad = inca.idindicadorcalidad; 
