use educayso_facae;
drop view jornadadocente1; 
create view jornadadocente1 as select jodo.idasignaturadocente,jodo.idjornadadocente,jodo.idaula,aula.nombre as elaula,jodo.horainicio,jodo.duracionminutos,dise.iddiasemana, dise.nombre ,concat(arco.nombre," - ",asig.nombre) as laasignatura,niac.nombre as nivel,para.nombre as paralelo,dido.iddistributivodocente from jornadadocente jodo,asignaturadocente asdo, diasemana dise,paralelo para,nivelacademico niac,asignatura asig,distributivodocente dido,areaconocimiento arco,aula aula where asdo.iddistributivodocente=dido.iddistributivodocente and  jodo.idasignaturadocente=asdo.idasignaturadocente and  jodo.iddiasemana=dise.iddiasemana and asdo.idasignatura=asig.idasignatura and asdo.idparalelo=para.idparalelo and asig.idnivelacademico=niac.idnivelacademico and asig.idareaconocimiento=arco.idareaconocimiento and jodo.idaula=aula.idaula;
