
use educayso_facae;
create view matricula1 as select matricula.idmatricula,matricula.idalumno,persona.idpersona,concat(COALESCE(persona.apellidos,''),"  ",COALESCE(persona.nombres,'')) as elalumno,matricula.iddepartamento,departamento.nombre as eldepartamento,(select repe.nombre from repeticion repe where repe.idrepeticion= matricula.idrepeticion) as repeticion,matricula.idperiodoacademico,periodoacademico.nombrecorto as elperiodo, (select tima.nombre from tipomatricula tima where tima.idtipomatricula=matricula.idtipomatricula) as eltipomatricula  from matricula,persona,departamento,alumno,periodoacademico  where matricula.idalumno=alumno.idalumno and alumno.idpersona=persona.idpersona and matricula.iddepartamento=departamento.iddepartamento and matricula.idperiodoacademico=periodoacademico.idperiodoacademico;
