
use educayso_facae;
drop view participacion2;
create view participacion2 as select par.idparticipacion,par.idevento,par.fecha,par.porcentaje,par.ayuda,par.idpersona,concat(per.apellidos," ",per.nombres) as nombres,par.comentario,sex.idsexo, sex.nombre as sexo,  (select ins.idinstitucion from estudio est, institucion ins where est.idnivelestudio=2 and est.idinstitucion=ins.idinstitucion and est.idpersona=per.idpersona) as idinstitucion ,(select ins.nombre from estudio est, institucion ins where est.idnivelestudio=2 and est.idinstitucion=ins.idinstitucion and est.idpersona=per.idpersona) as colegio, (select pat.idparticipanteestado from participante pat where pat.idpersona=per.idpersona and pat.idevento=par.idevento)  as idparticipanteestado,seev.idmodoevaluacion  from participacion par,persona0 per,sexo sex, sesionevento0 seev where par.idpersona=per.idpersona and per.idsexo=sex.idsexo and par.idevento=seev.idevento and par.fecha=seev.fecha;
