use educayso_facae; 
create view portafolio2 as select port.idportafolio,pele.idperiodoacademico, port.idpersona,pele.nombrecorto as elperiodo,concat(COALESCE(pers.apellidos,''),"  ",COALESCE(pers.nombres,'')) as lapersona,dopo.iddocumentoportafolio,docu.iddocumento,docu.asunto,docu.fechaelaboracion,docu.archivopdf,tido.descripcion as eltipodocumento, dire.ruta,orde.nombre as elordenador from portafolio port, periodoacademico pele, persona pers, documento docu, documentoportafolio dopo, tipodocu tido, directorio dire,ordenador orde where port.idperiodoacademico=pele.idperiodoacademico and port.idpersona=pers.idpersona and port.idportafolio=dopo.idportafolio and dopo.iddocumento=docu.iddocumento and docu.idtipodocu=tido.idtipodocu and docu.iddirectorio=dire.iddirectorio and docu.idordenador=orde.idordenador; 
