use educayso_facae;
create view  seguimientosilabo1 as select sesi.idseguimientosilabo,sesi.idsilabo, crse.idcriterioseguimientosilabo,crse.nombre as elcriterioseguimientosilabo,vcss.idvalorcriterioseguimientosilabo, vcss.nombre as elvalorcriterioseguimientosilabo   from seguimientosilabo sesi, criterioseguimientosilabo crse,valorcriterioseguimientosilabo vcss where sesi.idcriterioseguimientosilabo=crse.idcriterioseguimientosilabo and sesi.idvalorcriterioseguimientosilabo=vcss.idvalorcriterioseguimientosilabo;
