
use educayso_facae;
drop view silabo1;
create view silabo1 as select sila.idsilabo,sila.idperiodoacademico,sila.nombre as elsilabo, peac.nombrecorto as elperiodo,asig.idasignatura, asig.nombre as laasignatura, asig.codigo, sila.iddocente, concat(COALESCE(pers.apellidos,''),"  ",COALESCE(pers.nombres,''))  as eldocente,(select malla.nombrecorto from malla where malla.idmalla=asig.idmalla) as malla,sila.iddocumento, (select docu.asunto from documento docu where docu.iddocumento=sila.iddocumento) as asunto, (select docu.archivopdf from documento docu where docu.iddocumento=sila.iddocumento) as archivopdf, (select docu.archivopdf from documento docu, documentosilabo dosi where docu.iddocumento=dosi.iddocumento and dosi.idsilabo=sila.idsilabo limit 1 ) as elplandeclasepdf   from silabo sila,periodoacademico peac, asignatura asig, persona pers, docente doce  where sila.idperiodoacademico=peac.idperiodoacademico and sila.idasignatura=asig.idasignatura and sila.iddocente=doce.iddocente and doce.idpersona=pers.idpersona ;
