use educayso_facae; 
create view trabajointegracioncurricular3 as select ticu.idtrabajointegracioncurricular, dtic.iddocumentotrabajointegracioncurricular,docu.iddocumento,docu.asunto,docu.fechaelaboracion,docu.archivopdf,tido.descripcion as eltipodocumento, dire.ruta,orde.nombre as elordenador from trabajointegracioncurricular ticu,  documento docu, documentotrabajointegracioncurricular dtic, tipodocu tido, directorio dire,ordenador orde where ticu.idtrabajointegracioncurricular=dtic.idtrabajointegracioncurricular and dtic.iddocumento=docu.iddocumento and docu.idtipodocu=tido.idtipodocu and docu.iddirectorio=dire.iddirectorio and docu.idordenador=orde.idordenador; 
